<?php
namespace BrowserDetectorTest\Helper;

use BrowserDetector\Helper\FirefoxOs;

/**
 * Test class for KreditCore_Class_BrowserDetector.
 * Generated by PHPUnit on 2010-09-05 at 22:13:26.
 */
class FirefoxOsTest extends \PHPUnit_Framework_TestCase
{
    /**
     * @var \BrowserDetector\Input\Uaparser
     */
    private $object = null;

    /**
     * Sets up the fixture, for example, opens a network connection.
     * This method is called before a test is executed.
     */
    protected function setUp()
    {
        parent::setUp();

        $this->object = new FirefoxOs();
    }

    /**
     * Tears down the fixture, for example, closes a network connection.
     * This method is called after a test is executed.
     */
    protected function tearDown()
    {
        unset($this->object);

        parent::tearDown();
    }

    /**
     * @dataProvider providerIsFirefoxOsPositive
     */
    public function testIsFirefoxOsPositive($agent)
    {
        $this->object->setUserAgent($agent);

        self::assertTrue($this->object->isFirefoxOs());
    }

    public function providerIsFirefoxOsPositive()
    {
        return array(
            array('Mozilla/5.0 (Android; Tablet; rv:15.0) Gecko/15.0 Firefox/15.0.1'),
            array('Mozilla/5.0 (Android; Mobile; rv:15.0) Gecko/15.0 Firefox/15.0'),
            array('Mozilla/5.0 (Android; Tablet; rv:23.0) Gecko/23.0 Firefox/23.0'),
            array('Mozilla/5.0 (Android; Mobile; rv:16.0) Gecko/16.0 Firefox/16.0'),
            array('Mozilla/5.0 (Android; Tablet; rv:24.0) Gecko/24.0 Firefox/24.0'),
        );
    }
}